// app.component.ts

import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  selectedContent: number = 0;

  constructor(private router: Router) {}

  onOptionSelected(option: number): void {
    // Update the selected content based on the option clicked in the sidebar
    this.selectedContent = option;
  }

  navigateToNewPage(): void {
    // Replace 'new-page' with the route path of the new page you want to navigate to
    this.router.navigate(['new-page']);
  }
}
