// app.component.ts

import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  selectedContent: string = '';

  onOptionSelected(option: string): void {
    // Update the selected content based on the option clicked in the sidebar
    this.selectedContent = option;
  }
}
<!-- app.component.html -->

<!-- Include the topbar component -->
<app-topbar></app-topbar>

<div class="container-fluid">
  <div class="row">
    <div class="col-md-3">
      <!-- Include the sidebar component -->
      <app-sidebar></app-sidebar>
    </div>
    <div class="col-md-9">
      <!-- Add the content of your main component here -->
      <!-- This is where the content related to the selected option in the sidebar will be rendered -->
    </div>
  </div>
</div>
